select distinct i.PITEM_ID, ir.pitem_revision_id, wso.POBJECT_NAME, wso.pobject_type from infodba.pitem i, infodba.pworkspaceobject wso, infodba.pitemrevision ir where  i.puid=wso.puid and i.puid= ir.ritems_tagu  and wso.pobject_type='GET6Module' and ( i.PITEM_ID NOT LIKE '%AC-DENV' AND i.PITEM_ID NOT LIKE '%BC-DENV' AND i.PITEM_ID NOT LIKE '%LA-DENV' AND i.PITEM_ID NOT LIKE '%ML-DENV'AND i.PITEM_ID NOT LIKE '%PL-DENV' AND i.PITEM_ID NOT LIKE '%RC-DENV' AND i.PITEM_ID NOT LIKE '%TL-DENV' AND i.PITEM_ID NOT LIKE '%-MDENV');