select distinct i.pitem_id, wso.pobject_type, g.pname, u.pos_username from infodba.pitem i, infodba.ppom_application_object aom, infodba.ppom_group g, infodba.pworkspaceobject wso, infodba.puser u where i.puid=aom.puid and i.puid=wso.puid and wso.pobject_type in ( 'GET6Project', 'GET6Workarea', 'GET6Module') and aom.rowning_groupu=g.puid and aom.rowning_useru=u.puid and g.pname!='Transportation Systems';