SELECT DISTINCT I.PITEM_ID FROM PITEM I, PITEM I2, INFODBA.PWORKSPACEOBJECT WS, INFODBA.PIMANRELATION IR, INFODBA.PFOLDER F, INFODBA.PCONTENTS CONT WHERE IR.RPRIMARY_OBJECTU=I.PUID AND IR.RSECONDARY_OBJECTU=WS.PUID AND WS.POBJECT_TYPE='Folder' AND WS.POBJECT_NAME='Family Members' AND WS.PUID=F.PUID AND F.PUID=CONT.PUID AND I2.PUID=PVALU_0 AND I.PITEM_ID IN (SELECT DISTINCT I.PITEM_ID FROM INFODBA.PITEM I, INFODBA.PWORKSPACEOBJECT WS WHERE I.PITEM_ID NOT LIKE '%FAM%');