select  distinct i1.PITEM_ID, ir.pitem_revision_id, wso.pobject_name, wso.pobject_type, aom.pcreation_date, wso.pdate_released, aom.plast_mod_date from infodba.pitem i1, infodba.pworkspaceobject wso, infodba.pitemrevision ir, infodba.ppom_application_object aom where ir.puid=wso.puid and i1.puid=ir.ritems_tagu and wso.puid=aom.puid and wso.pdate_released is not null and wso.pdate_released < aom.plast_mod_date;